import { useState, useRef, useEffect } from "react";
import PhotoStrip from "./PhotoStrip";
import "./App.css";
import LivePhoto from "./LivePhoto";

function App() {
    const [imgSrc, setImgSrc] = useState("");
    const [sessionPhotos, setSessionPhotos] = useState([]);
    const webSocket = useRef(null);

    useEffect(() => {
        const ws_path =
            // "ws://" + window.location.host + window.location.pathname + "ws";
            "ws://localhost:8000/ws";
        webSocket.current = new WebSocket(ws_path);
        webSocket.current.onopen = () => {
            webSocket.current.send(1);
        };
        webSocket.current.onmessage = (msg) => {
            setImgSrc(`data:image/jpg;base64,${msg.data}`);
            webSocket.current.send(1);
        };
        webSocket.current.onerror = (e) => {
            console.log(e);
            webSocket.current.send(1);
        };
        return () => webSocket.current.close();
    }, []);

    const snap = () => {
        fetch("http://localhost:8000/snap", { method: "POST" });
        getData();
    };

    const getData = () => {
        fetch("http://localhost:8000/data")
            .then((res) => res.json())
            .then(
                (result) => {
                    console.log(result);
                    setSessionPhotos(result.currentCapture);
                },
                (error) => {
                    console.log(error);
                }
            );
    };

    return (
        <div className="App">
            <header className="App-header">
                <LivePhoto />
                <br />
                <PhotoStrip photos={sessionPhotos} />
            </header>
        </div>
    );
}

export default App;
