import logo from "./logo.svg";
import "./App.css";

function App() {
    const [imgSrc, setImgSrc] = useState("");
    const webSocket = useRef(null);

    useEffect(() => {
        const ws_path =
            // "ws://" + window.location.host + window.location.pathname + "ws";
            "ws://localhost:8000/ws";
        webSocket.current = new WebSocket(ws_path);
        webSocket.current.onopen = () => {
            ws.send(1);
        };
        webSocket.current.onmessage = (msg) => {
            console.log(msg);
            setImgSrc(`data:image/jpg;base64,${msg.data}`);
            ws.send(1);
        };
        webSocket.currentonerror = (e) => {
            console.log(e);
            ws.send(1);
        };
        return () => webSocket.current.close();
    }, []);
    // return <p>{messages.join(" ")}</p>;

    return (
        <div className="App">
            <header className="App-header">
                <img id="cam" src={imgSrc} alt="live camera view" />
                <p>
                    Edit <code>src/App.js</code> and save to reload.
                </p>
                <a
                    className="App-link"
                    href="https://reactjs.org"
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    Learn React
                </a>
            </header>
        </div>
    );
}

export default App;
