import logo from "./logo.svg";
import "./App.css";

function App() {
    let imgSrc = "";
    const ws_path =
        "ws://" + window.location.host + window.location.pathname + "ws";
    const ws = new WebSocket(ws_path);

    ws.onopen = () => {
        ws.send(1);
    };
    ws.onmessage = (msg) => {
        imgSrc = `data:image/jpg;base64,${msg.data}`;
        ws.send(1);
    };
    ws.onerror = (e) => {
        console.log(e);
        ws.send(1);
    };

    return (
        <div className="App">
            <header className="App-header">
                <img id="cam" src={imgSrc} alt="live camera view" />
                <p>
                    Edit <code>src/App.js</code> and save to reload.
                </p>
                <a
                    className="App-link"
                    href="https://reactjs.org"
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    Learn React
                </a>
            </header>
        </div>
    );
}

export default App;
